/**
 * 템플릿 ID로 템플릿 메타데이터 조회
 */
export function getTemplateById(id: string) {
  return TEMPLATE_CATALOG.find((t) => t.id === id)
}

/**
 * 모든 템플릿 ID 목록
 */
export function getAllTemplateIds(): string[] {
  return TEMPLATE_CATALOG.map((t) => t.id)
}
